import React,{useState} from 'react';

const AddArticle = (props) => {
    
    const [articleValues,setArticleValues]=useState({name:"",content:""});
    const { name,setArticlesInfo }=props;
    function handleChange(event){
        console.log(event.target);
        const {name,value}=event.target;
        setArticleValues({...articleValues,[name]:value});
    }
      async  function fetchContents(){
            const name=articleValues.username;
            const content=articleValues.content;
            const response = await fetch(`/api/articles/${name}/add-article`,{method:'post',
            body: JSON.stringify({name,content}),
            headers: {
                'Content-Type' : 'application/json'
            }
        })
          const body=await response.json();
          setArticlesInfo(body);
          setArticlesInfo({username:""},{comment:""})
          
    }

    return (
        <div>
            <label for='article-head'>Heading</label>
            <input type='text' name='article-head' onChange={handleChange}></input>
            <label for='content'>Content</label>
            <textarea rows='5' cols='5' onChange={handleChange}></textarea>
            <button onClick={fetchContents}>AddArticle</button>
        </div>
    );
};

export default AddArticle;
