import React,{useState,useEffect} from 'react';
import './Login.css'

const Login = () => {
 
	//   storing form values
    const [signupvalues,setSignupValues]=useState({username:'',email:'',password:'',repassword:''});
	// storing errors
	const [errorvalues,setErrorValues]=useState({})
	// to confirm button is clicked
	const [issubmit,setIsSubmit]=useState(false);

	const handlechange=(event)=>{
		const {name,value}=event.target;
		setSignupValues({...signupvalues,[name]:value});
	}

	const handlesubmit=(event)=>{
		event.preventDefault();
		setErrorValues(validation(signupvalues));
		setIsSubmit(true);
	}
	useEffect(()=>{
       if(Object.keys(errorvalues).length===0 && issubmit){
		   alert("Signup Successfull");
	   }
	},[errorvalues])



    return (
		<div className="login-wrap">
		<div className="login-html">
			<input id="tab-1" type="radio" name="tab" class="sign-in" checked></input><label for="tab-1" className="tab">Sign In</label>
			<input id="tab-2" type="radio" name="tab" class="sign-up"></input><label for="tab-2" className="tab">Sign Up</label>
			<div className="login-form">
				<div className="sign-in-htm">
					<div className="group">
						<label for="user" className="label">Username</label>
						<input id="user" type="text" className="input"></input>
					</div>
					<div class="group">
						<label for="pass" className="label">Password</label>
						<input id="pass" type="password" className="input" data-type="password"></input>
					</div>
					<div className="group">
						<input id="check" type="checkbox" className="check" checked></input>
						<label for="check"><span class="icon"></span> Keep me Signed in</label>
					</div>
					<div class="group">
						<input type="submit" className="button" value="Sign In"></input>
					</div>
					<div className="hr"></div>
					<div className="foot-lnk">
						<a href="#forgot">Forgot Password?</a>
					</div>
				</div>
				<div className="sign-up-htm">
					<div className="group">
						<label for="user" className="label">Username</label>
						<input id="user" type="text" className="input"></input>
					</div>
					<div className="group">
						<label for="pass" className="label">Password</label>
						<input id="pass" type="password" className="input" data-type="password"></input>
					</div>
					<div className="group">
						<label for="pass" className="label">Repeat Password</label>
						<input id="pass" type="password" className="input" data-type="password"></input>
					</div>
					<div className="group">
						<label for="pass" className="label">Email Address</label>
						<input id="pass" type="text" className="input"></input>
					</div>
					<div className="group">
						<input type="submit" className="button" value="Sign Up"></input>
					</div>
					<div className="hr"></div>
					<div className="foot-lnk">
						<label for="tab-1">Already Member?</label>
					</div>
				</div>
			</div>
		</div>
	</div>
    );
};

export default Login;
